import React, { Component } from 'react';
import axios from 'axios';
import { Sidebar, InputItem, DropdownItem, Icon, Item, Logo, LogoText } from 'react-sidebar-ui'
import {
  Row, Col, Card, CardHeader, CardBody, FormGroup, Label, Modal, Form, Input, InputGroup, InputGroupAddon,
  InputGroupText, Button, Collapse, FormText, Dropdown, DropdownToggle, DropdownMenu, ListGroup, ListGroupItem
} from "reactstrap";

const url = (process.env.REACT_APP_DOMAIN) + ':' + (process.env.REACT_APP_PORT) + '/';

class TestsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      accordion: [false, false],
      tests: [{
        "title": "test",
        "professor_id": "1",
        "max_score": "2",
        "questions": [
          {
            "title": "Ko je lenjivac",
            "points": "1",
            "answers": [
              {
                "title": "Zec",
                "isCorrect": 0
              },
              {
                "title": "Zmija",
                "isCorrect": 1
              },
              {
                "title": "Guster",
                "isCorrect": 1
              }
            ]
          },
          {
            "title": "Ko je nesto drugo",
            "points": "1",
            "answers": [
              {
                "title": "Zec",
                "isCorrect": 0
              },
              {
                "title": "Orao",
                "isCorrect": 1
              },
              {
                "title": "Vrabac",
                "isCorrect": 1
              }
            ]
          }
        ]
      },
      {
        "title": "Drugi test",
        "professor_id": "1",
        "max_score": "3",
        "questions": [
          {
            "title": "Pitanje 1",
            "points": "1",
            "answers": [
              {
                "title": "Odgovor1",
                "isCorrect": 0
              },
              {
                "title": "ODgvor 2",
                "isCorrect": 1
              },
              {
                "title": "Odgovor 2",
                "isCorrect": 1
              }
            ]
          },
          {
            "title": "Pitanje 2",
            "points": "2",
            "answers": [
              {
                "title": "Odgovor 21",
                "isCorrect": 0
              },
              {
                "title": "Odgovor2-2",
                "isCorrect": 1
              },
              {
                "title": "Odgovor 3-3",
                "isCorrect": 1
              }
            ]
          }
        ]
      }
      ]
    };

    this.getTests = this.getTests.bind(this);
    this.toggleAccordion = this.toggleAccordion.bind(this)

  }

  componentDidMount() {
    //this.getTests()
  }

  getTests() {
    axios({
      method: 'get',
      url: url + 'test',
    }).then((response) => {
      console.log(response);
      this.setState({ tests: response.data })
    }, (error) => {
      console.log(error);
    });
  }

  toggleAccordion(tab) {

    const prevState = this.state.accordion;
    const state = prevState.map((x, index) => tab === index ? !x : false);

    this.setState({
      accordion: state,
    });
  }

  render() {
    return (
      <div>
        <Button>
          <i class="fas fa-plus"></i>
        </Button>
        {(this.state.tests).map((test, index) =>
          <Row>
            <Col xs="12" md="12" sm="12">
              <Card style={{ backgroundColor: "lightblue", margin: "10px", padding:"2px"}}>
                <CardHeader>
                  <h1 hidden={!this.state.accordion[index]} >{test.title}
                    <i style={{marginLeft:"10px"}}  onClick={() => this.toggleAccordion(index)} class="fas fa-angle-up"></i>
                  </h1>
                  <h1 hidden={this.state.accordion[index]} >
                    {test.title}
                    <i style={{marginLeft:"10px"}} onClick={() => this.toggleAccordion(index)} class="fas fa-angle-down"></i>
                  </h1>
              {/*    <Button block color="link" className="text-center m-0 p-0" onClick={() => this.toggleAccordion(index)} aria-expanded={this.state.accordion[0]} aria-controls={index}>
                    <h5 className="m-0 p-0">Expand</h5>
                  </Button>            
        */}  
                  </CardHeader>
                <Collapse hidden={!this.state.accordion[index]} isOpen={this.state.accordion[index]} data-parent="#accordion" id={index} aria-labelledby={index}>
                  <CardBody style={{border:"3px solid lightblue", padding:"5px" }}>
                    {(test.questions).map((question, indexQ) =>
                      <Row>
                        <Card style={{ backgroundColor: "whitesmoke" }}>
                          <CardHeader style={{padding:"3px"}}>
                            {indexQ + 1}. {question.title}
                          </CardHeader>
                          <CardBody>
                            {(question.answers).map((answer, indexA) =>
                              <Card style={{ margin: "5px" }}>
                                <div>
                                  <input style={{marginRight:"10px"}} type="checkbox"></input>
                                  <label>{indexA + 1}. {answer.title}</label>
                                </div>
                              </Card>
                            )}
                          </CardBody>
                        </Card>
                      </Row>
                    )}
                  </CardBody>
                </Collapse>
              </Card>
            </Col>
          </Row>
        )}
      </div>
    );
  }
}

export default TestsPage;
