import React, { Component } from 'react';
import axios from 'axios';
import { Sidebar, InputItem, DropdownItem, Icon, Item, Logo, LogoText } from 'react-sidebar-ui'
import {Button, Card, CardBody, CardHeader, Row, Table, Col } from 'reactstrap';

const url = (process.env.REACT_APP_DOMAIN) + ':' + (process.env.REACT_APP_PORT) + '/';

class TestsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      test: {},
      testTake: {
          title: "",
          test_questions: [{
              title:"",
              test_question_answers:[]
          }]
      },
    };

    this.getTestTakeAnswer = this.getTestTake.bind(this)
    this.getTest = this.getTest.bind(this);
  }

  componentDidMount() {
    this.getTestTake()
  }

  getTest() {
    axios({
      method: 'get',
      url: url + 'test/' + this.state.testTake.test_id,
    }).then((response) => {
      this.setState({ test: response.data })
    }, (error) => {
      console.log(error);
    });
  }

  getTestTake(){
    const { id } = this.props.match.params;
    axios({
      method: 'get',
      url: url + 'test_take/' + id,
    }).then((response) => {
      this.setState({ testTake: response.data })
      this.getTest()
    }, (error) => {
      console.log(error);
    });
  }

  render() {
    return (
        <Card style={{ backgroundColor: "whiteSmoke" }}>
        <CardHeader>
            <h2>{this.state.test.title}</h2>
        </CardHeader>
        <CardBody>
          <Col md="12" lg="12" xs="12">
          <Row>
            <Col md="4">Question</Col>
            <Col md="4">Answer</Col>
            <Col md="4">Correct Answer</Col>
          </Row>
          {(this.state.test.test_questions).map((question, index) =>
          <div>
                  <Row>
                    <Col md="4"><h3>{index}. {question.title}</h3></Col>
                  </Row>
                  {(question.test_question_answers).map((answer, indexA) =>
                          <Row>
                          <Col md="4">{indexA}. {answer.title}</Col>
                          <Col md="4"> <insert type="checkbox" checked={true} disable={true}></insert></Col>
                          <Col md="4"> <insert type="checkbox" checked={answer.isCorrect} disable={true}></insert></Col>
                        </Row>
                        )}
                  </div>
                )}
          </Col>

        </CardBody>
      </Card>
    );
  }
}

export default TestsPage;
