import React, {Component, Suspense} from 'react';
import {Route, Switch} from 'react-router-dom';
import {Redirect} from 'react-router-dom';
//import * as router from 'react-router-dom';
import "../../../node_modules/react-notifications/lib/notifications.css"
import "../../../node_modules/react-notifications/lib/Notifications.js"
import axios from 'axios';
import {
  Container, Modal, ModalBody, ModalFooter, Label,
  Button, Form, Input, InputGroup, FormText,
  InputGroupAddon, InputGroupText, Row, ModalHeader, Nav
} from 'reactstrap';
import {Sidebar, InputItem, DropdownItem, Icon, Item, Logo, LogoText} from 'react-sidebar-ui'
import {NotificationContainer, NotificationManager} from 'react-notifications';

// sidebar nav config
import navigation from '../../_nav';
import adminnavigation from '../../nav_admin';
import studentnavigation from '../../nav_student';
import professornavigation from '../../nav_professor';
// routes config
import routes from '../../routes';
//import config from '../../config';
//import '../../scss/vendors/custom.css';

//const DefaultAside = React.lazy(() => import('./DefaultAside'));
//const DefaultHeader = React.lazy(() => import('./DefaultHeader'));

const url = (process.env.REACT_APP_DOMAIN) + ':' + (process.env.REACT_APP_PORT) + '/';

class DefaultLayout extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      formValid: true,   // enable/disable submit button depending on form validation
      notFilledError: true,
      formErrorText: "",
      passErrorText: "",
      usernameErrorText: "",
      changeFirstPass: false,
      password1: "",
      password11: "",
      password11Validation: "",
      password1Validation: "",
      modal: false,
      primary: false ,
      hideInputMail: true,
      emailErrorText: "" ,
      formValidMail: true ,
      email: ""
    };

    this.togglePrimary = this.togglePrimary.bind(this);
 //   this.navig = this.navig.bind(this);
  }

  togglePrimary() {
    this.setState({
      primary: !this.state.primary,
    });
  }



  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>    

/*
  navig = () => {
    if (localStorage.getItem("role") === "ROLE_ADMIN") {
      return adminnavigation;
    }else if (localStorage.getItem("role") === "ROLE_STUDENT") {
      return studentnavigation;
    }else if (localStorage.getItem("role") === "ROLE_PROFESSOR") {
      return professornavigation;
    } else {
      return navigation;
    }
*/




  navBar = () => {
    let ret = (<Sidebar bgColor='black' isCollapsed={false}>
    <Logo
      image='https://media2.giphy.com/media/eNAsjO55tPbgaor7ma/source.gif'
      imageName='react logo'/>
    <LogoText>React Sidebar UI</LogoText>
    <DropdownItem
      values={['First', 'Second', 'Third']}
      bgColor={'black'}>
      Menu
    </DropdownItem>

    <Item bgColor='black'>
      <Icon><i className="fas fa-home"/></Icon>
      Home
    </Item>
    <Item bgColor='black'>
      <Icon><i className="fas fa-info"/></Icon>
      About
    </Item>
    <Item bgColor='black'>
      <Icon><i className="fas fa-sitemap"/></Icon>
      My Website
    </Item>
    <Item bgColor='black'>
      <Icon><i className="far fa-address-book"/></Icon>
      Contacts
    </Item>
    <Item bgColor='black'>
      <Icon><i className="fas fa-rss-square"/></Icon>
      Blog
    </Item>
    <InputItem type='text' placeholder={'Search...'}/>
  </Sidebar>);

    if (localStorage.getItem('role'))
      return ret;
    else
      return <div></div>;
  };

  render() {
    return (
      <div className="app">
   {/*     <header fixed>
          <Suspense fallback={this.loading()}>
            <DefaultHeader onLogout={e => this.signOut(e)} onLogin={e => this.login(e)}/>
          </Suspense>
        </header>
   */}
        <div className="app-body">
          {this.navBar()}
          <main className="main bgimg">
            <br></br>
            <Container fluid>

              <Suspense fallback={this.loading()}>

                <Switch>
                  {routes.map((route, idx) => {
                    return route.component ? (
                      <Route authorize={route.authorize}
                             key={idx}
                             path={route.path}
                             exact={route.exact}
                             name={route.name}
                             render={props => (
                               <route.component {...props} />
                               )}/>
                    ) : (null);
                  })}
                  <Redirect from="/" to="/"/>
                </Switch>
              </Suspense>
            </Container>
          </main>
        </div>
        <Modal isOpen={this.state.primary} toggle={this.togglePrimary}
               className={'modal-primary ' + this.props.className}>
          <ModalHeader>Sign in to your account</ModalHeader>
          <ModalBody>
            <Form>

              <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                  <InputGroupText>
                    <i className="icon-user"></i>
                  </InputGroupText>
                </InputGroupAddon>
                <Input type="text" placeholder="Username" autoComplete="username" value={this.state.username}
                       onChange={event => this.setState({username: event.target.value})}
                       onBlur={this.validateUsername}/>
                <FormText color="danger">{this.state.usernameErrorText}</FormText>
              </InputGroup>
              <InputGroup className="mb-4">
                <InputGroupAddon addonType="prepend">
                  <InputGroupText>
                    <i className="icon-lock"></i>
                  </InputGroupText>
                </InputGroupAddon>
                <Input type="password" placeholder="Password" value={this.state.password}
                       onChange={event => this.setState({password: event.target.value})}/>
                <FormText color="danger">{this.state.passErrorText}</FormText>
              </InputGroup>
              <Label color="info" hidden={this.state.hideInputMail}>Enter your email and we will send you recovery details:</Label>
              <InputGroup className="mb-4" hidden={this.state.hideInputMail}>              
                <InputGroupAddon addonType="prepend">
                <InputGroupText>@</InputGroupText>
                </InputGroupAddon>
                <Input type="text" placeholder="email" value={this.state.email}
                       onChange={event => this.setState({email: event.target.value})}
                       onBlur={this.validateEmail}/>
                <FormText color="danger">{this.state.emailErrorText}</FormText>
              </InputGroup>
              <Row>
                <FormText color="danger">{this.state.formErrorText}</FormText>
              </Row>
              <Row className="text-center">
                <div class="text-center">
                <Button color="link" onClick={event => this.setState({hideInputMail: !this.state.hideInputMail})}>Forgot password?</Button> 
                </div>
              </Row>
            </Form>
          </ModalBody>
          <ModalFooter>
            {!this.state.hideInputMail && <Button color="primary" onClick={this.AccountRecovery} disabled={this.state.formValidMail}>Send email</Button>}{' '}
                {this.state.hideInputMail && <Button color="primary" onClick={this.sendLogin} disabled={this.state.formValid}>Log in</Button>}{' '}
            <Button color="secondary" onClick={this.togglePrimary}>Cancel</Button>
          </ModalFooter>
        </Modal>
        <NotificationContainer/>
      </div>
    );
  }
}

export default DefaultLayout;
